import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import Icon from 'react-icons/io';
import CustomNavigationBar from './components/CustomNavigationBar';
import All from './components/All';
import Sports from './components/Sports';
import Culture from './components/Culture';
import Research from './components/Research';
import { isMobile } from 'react-device-detect';

const App = () => {
  const history = Navigate();
  const [activeButton, setActiveButton] = useState('All');
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [selectedUniversity, setSelectedUniversity] = useState('');

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  const selectUniversity = (university) => {
    setSelectedUniversity(university);
    setIsMenuVisible(false);
  };

  const goToLogin = () => {
    history.push('/login');
    setIsMenuVisible(false);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#C1191A', padding: '10px 20px' }}>
        <h1 style={{ fontSize: '22px', color: '#fff' }}>
          歩くアルパカ {selectedUniversity ? selectedUniversity + 'サークル紹介' : '立命館サークル紹介'}
        </h1>
        <div onClick={toggleMenu} style={{ cursor: 'pointer' }}>
          <Icon name="menu" size={30} color={'#fff'} />
        </div>
      </div>
      {isMenuVisible && (
        <div style={{ position: 'absolute', width: isMobile ? '50%' : '20%', top: '70px', backgroundColor: '#C1191A', padding: '20px', zIndex: 1, right: 0 }}>
          <div onClick={goToLogin} style={{ cursor: 'pointer', marginBottom: '10px', fontSize: '20px', color: '#fff' }}>投稿</div>
          <div onClick={() => setSelectedUniversity('')} style={{ cursor: 'pointer', marginBottom: '10px', fontSize: '20px', color: '#fff' }}>切り替え</div>
          {selectedUniversity === '' && (
            <div style={{ backgroundColor: '#fff', maxHeight: '200px', overflowY: 'auto', borderRadius: '10px', padding: '10px' }}>
              <div onClick={() => selectUniversity('立命館大学')} style={{ cursor: 'pointer', marginBottom: '5px', fontSize: '14px', color: 'black' }}>立命館大学</div>
              <div onClick={() => selectUniversity('同志社大学')} style={{ cursor: 'pointer', marginBottom: '5px', fontSize: '14px', color: 'black' }}>同志社大学</div>
              <div onClick={() => selectUniversity('関西大学')} style={{ cursor: 'pointer', marginBottom: '5px', fontSize: '14px', color: 'black' }}>関西大学</div>
              <div onClick={() => selectUniversity('関西学院大学')} style={{ cursor: 'pointer', marginBottom: '5px', fontSize: '14px', color: 'black' }}>関西学院大学</div>
              {/* 必要に応じて他の大学を追加 */}
            </div>
          )}
        </div>
      )}
      <CustomNavigationBar activeButton={activeButton} setActiveButton={setActiveButton} />
      <div style={{ flex: 1 }}>
        {activeButton === 'All' && <All selectedUniversity={selectedUniversity} />}
        {activeButton === 'Sports' && <Sports selectedUniversity={selectedUniversity} />}
        {activeButton === 'Culture' && <Culture selectedUniversity={selectedUniversity} />}
        {activeButton === 'Research' && <Research selectedUniversity={selectedUniversity} />}
      </div>
    </div>
  );
};

export default App;
