import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, createUserWithEmailAndPassword, db, collection, addDoc } from '../firebase';

const Registration = () => {
  const navigate = useNavigate();
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');
  const [username, setUsername] = useState('');

  const addUserData = async (userId, username) => {
    try {
      await addDoc(collection(db, 'User'), {
        UserID: userId,
        Username: username,
        Email: registerEmail
      });
    } catch (error) {
      console.error('Error adding user data:', error.message);
      throw error;
    }
  };

  const handleSubmit = async () => {
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        registerEmail,
        registerPassword
      );
      const user = userCredential.user;

      await addUserData(user.uid, username);

      navigate('/post', { state: { userData: { UserID: user.uid, Username: username } } });
    } catch (error) {
      console.error('Error registering user:', error.message);
      alert('正しく入力してください');
    }
  };

  return (
    <div>
      <div style={{ alignItems: 'center', marginTop: '10%' }}>
        <h2>新規登録</h2>
      </div>
      <div style={{ alignItems: 'center', marginTop: '1%' }}>
        <label>メールアドレス</label>
        <input
          type="email"
          value={registerEmail}
          onChange={(e) => setRegisterEmail(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <label style={{ marginTop: '1%' }}>パスワード</label>
        <input
          type="password"
          value={registerPassword}
          onChange={(e) => setRegisterPassword(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <label style={{ marginTop: '1%' }}>ユーザー名</label>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <div style={{ marginTop: '2%', width: '30%' }}>
          <button onClick={handleSubmit} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A' }}>
            <span style={{ marginLeft: '21%', color: 'white' }}>登録する</span>
          </button>
        </div>
        <p>・パスワードは６文字以上で設定してください。</p>
      </div>
    </div>
  );
};

export default Registration;
