import React from 'react';

const CustomNavigationBar = ({ activeButton, setActiveButton }) => {
  const handlePress = (buttonName) => {
    setActiveButton(buttonName);
  };

  return (
    <div style={styles.container}>
      <div style={styles.buttonsContainer}>
        <button onClick={() => handlePress('All')} style={[styles.button, activeButton === 'All' && styles.activeButton]}>
          <span>ALL</span>
        </button>
        <button onClick={() => handlePress('Sports')} style={[styles.button, activeButton === 'Sports' && styles.activeButton]}>
          <span>体育会系</span>
        </button>
        <button onClick={() => handlePress('Culture')} style={[styles.button, activeButton === 'Culture' && styles.activeButton]}>
          <span>文化系</span>
        </button>
        <button onClick={() => handlePress('Research')} style={[styles.button, activeButton === 'Research' && styles.activeButton]}>
          <span>研究系</span>
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    paddingBottom: 10,
    borderBottom: '1px solid #ccc',
  },
  buttonsContainer: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    padding: '0 5%',
  },
  button: {
    flex: 1,
    textAlign: 'center',
    marginTop: '1%',
    border: 'none',
    background: 'none',
    cursor: 'pointer',
    outline: 'none',
  },
  activeButton: {
    borderBottom: '2px solid blue',
  },
};

export default CustomNavigationBar;
