import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { IconContext } from 'react-icons';
import { FaBars } from 'react-icons/fa';
import CustomNavigationBar from './components/CustomNavigationBar';
import All from './components/All';
import Sports from './components/Sports';
import Culture from './components/Culture';
import Research from './components/Research';
import Login from './components/Login';
import Registration from './components/Registration';
import Post from './components/Post';
import './App.css';

const AppWrapper = () => {
  const [activeButton, setActiveButton] = useState('All');
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const [selectedUniversity, setSelectedUniversity] = useState('');

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  const selectUniversity = (university) => {
    setSelectedUniversity(university);
    setIsMenuVisible(false);
  };

  return (
    <div className="container">
      <div className="header">
        <h1 className="title">
          歩くアルパカ {selectedUniversity ? `${selectedUniversity}サークル紹介` : '立命館サークル紹介'}
        </h1>
        <button onClick={toggleMenu} className="menuButton">
          <IconContext.Provider value={{ size: '2em', color: '#fff' }}>
            <FaBars />
          </IconContext.Provider>
        </button>
      </div>
      {isMenuVisible && (
        <div className="menu">
          <Link to="/post" className="menuItem" onClick={() => setIsMenuVisible(false)}>投稿</Link>
          <button className="menuItem" onClick={() => setSelectedUniversity('')}>切り替え</button>
          {selectedUniversity === '' && (
            <div className="universityMenu">
              <button onClick={() => selectUniversity('立命館大学')} className="universityMenuItem">立命館大学</button>
              <button onClick={() => selectUniversity('同志社大学')} className="universityMenuItem">同志社大学</button>
              <button onClick={() => selectUniversity('関西大学')} className="universityMenuItem">関西大学</button>
              <button onClick={() => selectUniversity('関西学院大学')} className="universityMenuItem">関西学院大学</button>
              {/* 必要に応じて他の大学を追加 */}
            </div>
          )}
        </div>
      )}
      <CustomNavigationBar activeButton={activeButton} setActiveButton={setActiveButton} />
      <div className="contentContainer">
        {activeButton === 'All' && <All selectedUniversity={selectedUniversity} />}
        {activeButton === 'Sports' && <Sports selectedUniversity={selectedUniversity} />}
        {activeButton === 'Culture' && <Culture selectedUniversity={selectedUniversity} />}
        {activeButton === 'Research' && <Research selectedUniversity={selectedUniversity} />}
      </div>
    </div>
  );
};

const App = () => (
  <Router>
    <Routes>
      <Route path="/" exact component={AppWrapper} />
      <Route path="/login" component={Login} />
      <Route path="/registration" component={Registration} />
      <Route path="/post" component={Post} />
    </Routes>
  </Router>
);

export default App;
