import React, { useState } from 'react';
import { Navigate } from 'react-router-dom'; // React RouterのuseHistoryを使用
import { auth, db } from '../firebase'; // firebase.jsからauthとdbをインポート
import { collection, addDoc} from 'firebase/firestore'; // Firestore関連のメソッドをインポート
import { isMobile } from 'react-device-detect';

const Registration = () => { 
  const history = Navigate();
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');
  const [username, setUsername] = useState('');

  const handleSubmit = async () => {
    try {
      const userCredential = await auth.createUserWithEmailAndPassword(
        registerEmail,
        registerPassword
      );
      const user = userCredential.user;

      // Firestoreにユーザーのデータを保存
      await addUserData(user.uid, username);

      // 登録が成功した場合の処理
      history.push('/post');
    } catch(error) {
      console.error('Error registering user:', error.message);
      alert('正しく入力してください');
    }
  };

  // Firestoreにユーザーのデータを保存する関数
  const addUserData = async (userId, username) => {
    try {
      // 'User'コレクションにユーザーのデータを追加
      await addDoc(collection(db, 'User'), {
        UserID: userId,
        Username: username
      });
    } catch (error) {
      console.error('Error adding user data:', error.message);
    }
  };

  return (
    <div>
      <div style={{alignItems:'center',marginTop:'10%'}}>
        <h2>新規登録</h2>
      </div>
      <div style={{alignItems:'center',marginTop:'1%'}}>
        <p>メールアドレス</p>
        <input
          value={registerEmail}
          onChange={(e) => setRegisterEmail(e.target.value)}
          type="email"
          style={{borderWidth:1,borderRadius:5,width: isMobile ? '50%' : '30%'}}
        />
        <p style={{marginTop:'1%'}}>パスワード</p>
        <input
          value={registerPassword}
          onChange={(e) => setRegisterPassword(e.target.value)}
          type="password"
          style={{borderWidth:1,borderRadius:5,width: isMobile ? '50%' : '30%'}}
        />
        <p style={{marginTop:'1%'}}>ユーザー名</p>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{borderWidth:1,borderRadius:5,width: isMobile ? '50%' : '30%'}}
        />
        <div style={{marginTop:'2%', width: isMobile ? '30%': '15%'}}>
          <button onClick={handleSubmit} style={{borderWidth:1, borderRadius:10, backgroundColor:'#C1191A', color:'white'}}>
            登録する
          </button>
        </div>
        <p>・パスワードは６文字以上で設定してください。</p>
      </div>
    </div>
  );
};

export default Registration;
