  projectId: "tokusetu-c2b0c",
  storageBucket: "tokusetu-c2b0c.appspot.com",
  messagingSenderId: "413732280630",
  appId: "1:413732280630:web:84845fe01176ccd8dcbb97",
  measurementId: "G-YVC5JWL368"
};

