import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory instead of useNavigation
import { auth, db } from '../firebase'; // Import auth and db
import { collection, getDocs, query, where } from 'firebase/firestore'; // Import Firestore methods

const Login = () => {
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const goToRegistration = () => {
    history.push('/registration'); // Use history.push instead of navigation.navigate
  };

  const handleLogin = async () => {
    try {
      const userCredential = await auth.signInWithEmailAndPassword(email, password); // Remove auth from signInWithEmailAndPassword
      const user = userCredential.user;

      // Get user data
      const userData = await getUserData(user.uid);

      // Navigate to the Post component after login with user data
      history.push('/post', { userData });
    } catch (error) {
      // Handle login errors
      alert('Login Error: ' + error.message);
    }
  };

  // Function to get user data from Firestore
  const getUserData = async (userId) => {
    try {
      const userQuery = query(collection(db, 'User'), where('UserID', '==', userId));
      const querySnapshot = await getDocs(userQuery);
      const userData = [];
      querySnapshot.forEach((doc) => {
        userData.push({ id: doc.id, ...doc.data() });
      });
      return userData[0]; // Return the first document if multiple are found
    } catch (error) {
      console.error('Error getting user data:', error.message);
      return null;
    }
  };

  return (
    <div>
      <div style={{ textAlign: 'center', marginTop: '10%' }}>
        <h2>ログイン画面</h2>
      </div>
      <div style={{ textAlign: 'center', marginTop: '1%' }}>
        <label htmlFor="email">メールアドレス</label>
        <br />
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '30%' }}
        />
        <br />
        <label htmlFor="password">パスワード</label>
        <br />
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '30%' }}
        />
        <br />
        <div style={{ marginTop: '2%', width: '15%' }}>
          <button onClick={handleLogin} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A' }}>
            ログイン
          </button>
          <br />
          <button onClick={goToRegistration} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A', marginTop: '3%' }}>
            新規登録
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
