import React, { useEffect, useState } from 'react';
import { getFirestore, collection, query, where, getDocs } from 'firebase/firestore';
import { getStorage, ref, getDownloadURL } from 'firebase/storage';
import { isMobile } from 'react-device-detect';

const Culture = ({ selectedUniversity }) => {
  const [posts, setPosts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const db = getFirestore();
  const storage = getStorage();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const q = query(
          collection(db, 'Post'),
          where('college', '==', selectedUniversity),
          where('Field.field', '==', '文化系')
        );
        const querySnapshot = await getDocs(q);
        const fetchedPosts = [];

        await Promise.all(
          querySnapshot.docs.map(async (doc) => {
            const postData = doc.data();
            const imageRef = ref(storage, `images/${postData.userID}/${postData.Field.Content.imageRandomId}`);
            const imageUrl = await getDownloadURL(imageRef);
            postData.imageUrl = imageUrl;
            fetchedPosts.push({ id: doc.id, ...postData });
          })
        );

        setPosts(fetchedPosts);
      } catch (error) {
        console.error('投稿の取得エラー:', error.message);
      }
    };

    fetchPosts();
  }, [selectedUniversity, db, storage]);

  const nextPosts = () => {
    if (currentIndex + 4 < posts.length) {
      setCurrentIndex(currentIndex + 4);
    }
  };

  const prevPosts = () => {
    if (currentIndex - 4 >= 0) {
      setCurrentIndex(currentIndex - 4);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.row}>
        {posts.slice(currentIndex, currentIndex + 2).map((post) => (
          <div key={post.id} style={styles.postContainer}>
            <div style={styles.postContent}>
              {post.imageUrl && <img src={post.imageUrl} alt="Post" style={styles.image} />}
              <div style={styles.textContainer}>
                <p>ユーザー名: {post.username}</p>
                <p>投稿内容: {post.Field.Content.contents}</p>
                <p>活動場所: {post.Field.Content.actplace}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div style={styles.row}>
        {posts.slice(currentIndex + 2, currentIndex + 4).map((post) => (
          <div key={post.id} style={styles.postContainer}>
            <div style={styles.postContent}>
              {post.imageUrl && <img src={post.imageUrl} alt="Post" style={styles.image} />}
              <div style={styles.textContainer}>
                <p>ユーザー名: {post.username}</p>
                <p>投稿内容: {post.Field.Content.contents}</p>
                <p>活動場所: {post.Field.Content.actplace}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div style={styles.buttonContainer}>
        <button onClick={prevPosts} style={styles.button}>前へ</button>
        <button onClick={nextPosts} style={styles.button}>次へ</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    flex: 1,
    padding: 10,
  },
  row: {
    flexDirection: isMobile ? 'column' : 'row',
    marginBottom: isMobile ? '2%' : 20,
  },
  postContainer: {
    flex: 1,
    backgroundColor: '#f0f0f0',
    paddingLeft: '10%',
    borderRadius: 8,
  },
  postContent: {
    flexDirection: 'row',
    paddingTop: isMobile ? '5%' : 0
  },
  textContainer: {
    flex: 1,
    paddingTop: '3%'
  },
  image: {
    width: isMobile ? 100 : 200,
    height: isMobile ? 100 : 200,
    borderRadius: 8,
    marginRight: 10,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 20,
  },
  button: {
    backgroundColor: '#C1191A',
    width: isMobile ? '10%' : '4%',
    height: '120%',
    borderRadius: 5,
    color: '#fff',
    margin: 'auto'
  }
};

export default Culture;
