import React, { useState } from 'react';
import { auth, createUserWithEmailAndPassword } from './firebase';

const Registration = ({ navigation }) => { 
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');
  const [username, setUsername] = useState('');

  const handleSubmit = async () => {
    try {
        const userCredential = await createUserWithEmailAndPassword(
            auth,
            registerEmail,
            registerPassword
          );
          const user = userCredential.user;

          // Firestoreにユーザーのデータを保存
          await addUserData(user.uid, username);

          // 登録が成功した場合の処理
          navigation.navigate('Post');
    } catch(error) {
      console.error('Error registering user:', error.message);
      alert('正しく入力してください');
    }
  };

  return (
    <div>
      <div style={{ alignItems: 'center', marginTop: '10%' }}>
        <h2>新規登録</h2>
      </div>
      <div style={{ alignItems: 'center', marginTop: '1%' }}>
        <label>メールアドレス</label>
        <input
          type="email"
          value={registerEmail}
          onChange={(e) => setRegisterEmail(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <label style={{ marginTop: '1%' }}>パスワード</label>
        <input
          type="password"
          value={registerPassword}
          onChange={(e) => setRegisterPassword(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <label style={{ marginTop: '1%' }}>ユーザー名</label>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '50%' }}
        />
        <div style={{ marginTop: '2%', width: '30%' }}>
          <button onClick={handleSubmit} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A' }}>
            <span style={{ marginLeft: '21%', color: 'white' }}>登録する</span>
          </button>
        </div>
        <p>・パスワードは６文字以上で設定してください。</p>
      </div>
    </div>
  );
};

export default Registration;
