import React, { useEffect, useState } from 'react';
import { db, storage } from '../firebase';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { ref, getDownloadURL } from 'firebase/storage';
import './styles.css'; // スタイル用のCSSファイル

const All = ({ selectedUniversity }) => {
  const [posts, setPosts] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        if (!selectedUniversity) return; // No need to fetch if no university selected

        const q = query(
          collection(db, 'Post'),
          where('college', '==', selectedUniversity), // Correct university filter
          where('Field.field', 'in', ['体育系', '研究系', '文化系'])
        );

        const querySnapshot = await getDocs(q);
        const fetchedPosts = [];

        console.log("querySnapshot", querySnapshot.docs); // Check if querySnapshot is logged

        querySnapshot.forEach((doc) => {
          const postData = doc.data();
          const imageRef = ref(storage, `images/${postData.userID}/${postData.Field.Content.imageRandomId}`);

          // Fetching image URL
          getDownloadURL(imageRef)
            .then((imageUrl) => {
              fetchedPosts.push({ id: doc.id, ...postData, imageUrl });
              console.log("fetchedPosts", fetchedPosts); // Check fetchedPosts content
              setPosts(fetchedPosts); // Update state with fetched posts
            })
            .catch((error) => {
              console.error('Error getting image URL:', error);
            });
        });
      } catch (error) {
        console.error('fetchPosts内でエラーが発生しました:', error);
      }
    };

    fetchPosts(); // Fetch posts when selectedUniversity changes
  }, [selectedUniversity]);






  const nextPosts = () => {
    if (currentIndex + 4 < posts.length) {
      setCurrentIndex(currentIndex + 4);
    }
  };

  const prevPosts = () => {
    if (currentIndex - 4 >= 0) {
      setCurrentIndex(currentIndex - 4);
    }
  };

  const PostItem = ({ post }) => {
    console.log('post プロパティの値:', post);

    return (
      <div className="postContainer" style={{ flex: 1 }}>
        <div className="postContent">
          {post.imageUrl && <img src={post.imageUrl} className="image" alt="Post" />}
          <div className="textContainer" style={{ flex: 1 }}>
            <p>ユーザー名: {post.username}</p>
            <p>投稿内容: {post.Field?.Content?.contents}</p>
            <p>活動場所: {post.Field?.Content?.actplace}</p>
          </div>
        </div>
      </div>
    );
  };



  return (
    <div className="container">
      <div className="row">
        {posts.slice(currentIndex, currentIndex + 2).map((post) => (
          <PostItem key={post.id} post={post} />
        ))}
      </div>
      <div className="row">
        {posts.slice(currentIndex + 2, currentIndex + 4).map((post) => (
          <PostItem key={post.id} post={post} />
        ))}
      </div>
      <div className="buttonContainer">
        <button onClick={prevPosts} className="button">前へ</button>
        <button onClick={nextPosts} className="button">次へ</button>
      </div>
    </div>
  );
};

export default All;
