import React, { useState } from 'react';
import { Navigate } from 'react-router-dom'; // React RouterのuseHistoryを使用
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth, db } from '../firebase'; // authとdbをインポート
import { collection, getDocs, query, where } from 'firebase/firestore'; // Firestore関連のメソッドをインポート

const Login = () => {
  const history = Navigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const goToRegistration = () => {
    history.push('/registration'); // React Routerのhistory.pushを使用
  };

  const handleLogin = async () => {
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // ユーザーのデータを取得
      const userData = await getUserData(user.uid);

      // ログイン後にユーザーのデータとともにPostコンポーネントに移動
      history.push('/post', { userData });
    } catch (error) {
      // Handle login errors
      console.error('Login Error:', error.message);
      // Handle login errors using alert or any other method you prefer
    }
  };

  // Firestoreからユーザーのデータを取得する関数
  const getUserData = async (userId) => {
    try {
      const userQuery = query(collection(db, 'User'), where('UserID', '==', userId));
      const querySnapshot = await getDocs(userQuery);
      const userData = [];
      querySnapshot.forEach((doc) => {
        userData.push({ id: doc.id, ...doc.data() });
      });
      return userData[0]; // 複数のドキュメントが見つかった場合、最初のものを返す
    } catch (error) {
      console.error('Error getting user data:', error.message);
      return null;
    }
  };

  return (
    <div>
      <div style={{ alignItems: 'center', marginTop: '10%' }}>
        <h2>ログイン画面</h2>
      </div>
      <div style={{ alignItems: 'center', marginTop: '1%' }}>
        <label>メールアドレス:</label>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '30%' }}
        />
        <label style={{ marginTop: '1%' }}>パスワード:</label>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ borderWidth: 1, borderRadius: 5, width: '30%' }}
        />
        <div style={{ marginTop: '2%', width: '15%' }}>
          <button onClick={handleLogin} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A', color: 'white' }}>
            ログイン
          </button>
          <button onClick={goToRegistration} style={{ borderWidth: 1, borderRadius: 10, backgroundColor: '#C1191A', marginTop: '3%', color: 'white' }}>
            新規登録
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
